package com.java.thinking.leetcode.mid;

import java.util.ArrayList;
import java.util.LinkedList;
import java.util.List;
import java.util.Queue;

public class CharacterReplacement {
	public int characterReplacement(String s, int k) {
		if (s == null) {
			return 0;
		}
		char[] seq = s.toCharArray();
		int maxCount = 0;
		List<Character> list = new ArrayList<Character>(26);
		for (int i = 0; i < seq.length; i++) {
			char temCh = seq[i];
			if (list.contains(temCh)) {// AABABBA
				// 已经统计，则跳过
				continue;
			}
			list.add(temCh);
			int temCount = 1;
			int temK = k;
			Queue<Integer> queue = new LinkedList<Integer>();
			int temSum = 0;
			// 是否用完过k
			boolean beenTail = false;
			int startIndex = i;
			// 使用贪心算法，认为前面的数最大，得到后赋值；再往后继续计算，将前面元素抛弃，继续求最大值
			for (int j = i + 1; j < seq.length; j++) {
				if (beenTail) {
					startIndex = j;
				}
				if (beenTail && temCh != seq[j]) {
					// 已经查询到结尾，则需要重新查找
					continue;
				}
				beenTail = false;
				if (temCh == seq[j]) {
					temCount++;
				} else {
					if (temK > 0) {
						// 使用空格
						temK--;
						// 第x个空格，附带了几个元素-包含空格
						queue.offer(++temCount - temSum);
						// 替换了一个元素
						temSum = temCount;
					} else {
						// 替换元素已用完，超过最大值
						maxCount = Math.max(temCount, maxCount);
						if (queue.size() > 0) {
							// 本元素需要替换，同时删除一个空格后，空格要添加，两相抵消
							int removeCount = queue.poll();
							// 减去头部，包含的元素个数
							temSum = temCount -= removeCount;
							queue.offer(++temCount - temSum);
							// 替换了一个元素
							temSum = temCount;
							startIndex++;
							while (queue.size() > 0 && queue.peek() == 1) {
								// 如果没有temChar，只是空格，则需要清理头部元素
								queue.poll();
								temSum = --temCount;
								temK++;
								startIndex++;
							}
						} else {
							// 已经没有可以替换的空格，则需要清空数据，重新查找
							temCount = temSum = 0;
							temK = k;
							beenTail = true;
						}
					}
				}
			}
			if (temK > 0) {
				// 多出来没有使用的的空格
				for (int j = startIndex - 1; j >= 0; j--) {
					if (temCh == seq[j]) {
						temCount++;
					} else {
						if (temK-- > 0) {
							temCount++;
						} else {
							break;
						}
					}
				}
			}
			// 替换元素已用完，超过最大值
			maxCount = Math.max(temCount, maxCount);
		}
		return maxCount;
	}

	public int characterReplacement2(String s, int k) {
		int len = s.length();
		if (len < 2) {
			return len;
		}
		char[] charArray = s.toCharArray();
		int left = 0;
		int right = 0;

		int ans = 0;
		int maxCount = 0;
		int[] freq = new int[26];
		// [left, right) 内最多替换 k 个字符可以得到只有一种字符的子串
		while (right < len) {
			freq[charArray[right] - 'A']++;
			// 在这里维护 maxCount，因为每一次右边界读入一个字符，字符频数增加，才会使得 maxCount 增加
			maxCount = Math.max(maxCount, freq[charArray[right] - 'A']);
			right++;

			if (right - left > maxCount + k) {
				// 说明此时 k 不够用
				// 把其它不是最多出现的字符替换以后，都不能填满这个滑动的窗口，这个时候须要考虑左边界向右移动
				// 移出滑动窗口的时候，频数数组须要相应地做减法
				freq[charArray[left] - 'A']--;
				left++;
			}
			ans = Math.max(ans, right - left);
		}
		return ans;
	}

	public int characterReplacement3(String s, int k) {
		int[] num = new int[26];
		int n = s.length();
		int maxn = 0;
		int left = 0, right = 0;
		while (right < n) {
			num[s.charAt(right) - 'A']++;
			maxn = Math.max(maxn, num[s.charAt(right) - 'A']);
			if (right - left + 1 - maxn > k) {
				num[s.charAt(left) - 'A']--;
				left++;
			}
			right++;
		}
		return right - left;
	}

	public static void main(String[] args) {
		CharacterReplacement replacement = new CharacterReplacement();
		String s = "";
		int k = 100;
		// s = "ABCDE";
		// k = 1;
		// s =
		// "";
		// k = 80;
		// s="";
		// k=4567;
		s = "BRJRRKNRBFOOKDEEGODTGMHNABMTHFNPTFRHRSEKKTFEQIKSIAJJMSDSLNSCNRNJFNFSIQDNMHDRIJIACLCJKATTFHDASGLRQSFN";
		s = "NABMTHFNPTFRHRSEKKTFEQIKSIAJJMSDSLNSCNRNJFNFSIQDNM";
		k = 10;
		s = "AABABBA";
		k = 1;
//		s = "ABBB";
//		k = 2;
//		s = "KRSCDCSONAJNHLBMDQGIFCPEKPOHQIHLTDIQGEKLRLCQNBOHNDQGHJPNDQPERNFSSSRDEQLFPCCCARFMDLHADJADAGNNSBNCJQOF";
//		k = 4;
//		s = "ABAB";
//		k = 2;
		System.out.println(replacement.characterReplacement2(s, k));

	}
}
